import './App.css';

import React, { useState } from 'react';
import { useNavigate } from "react-router";

import { Typography, Accordion, AccordionSummary, AccordionDetails } from "@material-ui/core";
import ExpandMoreIcon from "@material-ui/icons/ExpandMore";
import Box from '@mui/material/Box';
import Fab from '@mui/material/Fab';
import MenuOutlinedIcon from '@mui/icons-material/MenuOutlined';
import Drawer from '@mui/material/Drawer';
import Stack from '@mui/material/Stack';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';

const App = () => {

  const nav = useNavigate();
  let token = localStorage.getItem('token')
  console.log(token);
  if (token) localStorage.setItem('token', token);


  const headers = { Authorization: `Bearer ${localStorage.getItem('token')}` };

  const [menuClicked, setMenuClicked] = useState(false);
  const [user, setUser] = useState({
    name: "",
    email: "",
    password: ""
  })
  const [activeUser, setActiveUser] = useState({
    name: "",
    email: "",
    password: ""
  })

  const updateUser = (v) => {
    return setUser((prev) => {
      console.log({...user, ...v});
      return {...user, ...v};
    });
  }

  async function onUserSubmit(e) {
    e.preventDefault();

    const newPerson = {...user};

    await fetch("http://localhost:5000/user/register", {
      method: "POST",
      headers: {
        "Content-type": "application/json",
      },
      body: JSON.stringify(newPerson),
    })
    .then(() => {
      setActiveUser(newPerson);
      setUser({ name: "", email: "", password: ""});
      nav('/');
    })
    .catch(e => {
      window.alert(e);
      console.log(e);
      return;
    });
  }

  async function onUserLogin(e) {
    e.preventDefault();

    const login = { email: user.email, password: user.password};

    await fetch("http://localhost:5000/user/login", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(login),
    })
    .then(() => {
      setUser({ name: "", email: "", password: ""});
      nav('/');
    })
    .catch(e => {
      window.alert(e);
      console.log(e);
      return;
    })
  }

  return (
    <Box className="homeApp">
      <div className="homeHeader">
        <Fab variant="extended" size="large" aria-label="add">App Name</Fab>
        <Typography variant="h4">User Profile</Typography>
        <Accordion>
        <AccordionSummary expandIcon={<ExpandMoreIcon />}>
          <Typography>Account Information</Typography>
        </AccordionSummary>
        <AccordionDetails>
          <Typography>
            Email: {userData.email}
            <br />
            Name: {userData.name}
          </Typography>
        </AccordionDetails>
        </Accordion>
      <Accordion>
        <AccordionSummary expandIcon={<ExpandMoreIcon />}>
          <Typography>Settings</Typography>
        </AccordionSummary>
        <AccordionDetails>
          <Typography>
            {/* User settings */}
          </Typography>
        </AccordionDetails>
      </Accordion> 
      </div>

              {/* <form onSubmit={onUserSubmit}>
                <TextField label="Full Name" variant="standard" onChange={e => updateUser({ name: e.target.value })}/>
                <TextField label="Email" variant="standard" onChange={e => updateUser({ email: e.target.value })}/>
                <TextField label="Password" variant="standard" onChange={e => updateUser({ password: e.target.value })}/>
                <Button type="submit" variant="contained">Create User</Button>
              </form>

              <form onSubmit={onUserLogin}>
                <TextField label="Email" variant="standard" onChange={e => updateUser({ email: e.target.value })}/>
                <TextField label="Password" variant="standard" onChange={e => updateUser({ password: e.target.value })}/>
                <Button type="submit" variant="contained">Login</Button>
              </form> */}

      <div className="homeBody">

      </div>

    </Box>
  );
}

export default App;
